import React,{useState} from 'react'
import "./SearchBar.css"
import SearchIcon from '@mui/icons-material/Search';
import CloseIcon from '@mui/icons-material/Close';
import TableData from './TableData';
import { NavLink, Route, Routes } from 'react-router-dom';
import Details from './Details';


function SearchBar({ placeholder, data ,SetElement,element}) {
  const [filteredData, setFilteredData] = useState([]);
  const [wordEntered, setWordEntered] = useState("");
  const [country,setCountry] = useState(filteredData);


  const handleFilter = (event) => {
    const searchWord = event.target.value;
    setWordEntered(searchWord);
    const newFilter = data.filter((value) => {
      return value.name.toLowerCase().includes(searchWord.toLowerCase());
    });

    if (searchWord === "") {
      setFilteredData([]);
    } else {
      setFilteredData(newFilter);
      setCountry(newFilter);
    }
  };

  const clearInput = () => {
    setFilteredData([]);
    setWordEntered("");
  };

  const onSearch = () =>{
    SetElement(filteredData[0].name);




  }
  const format =() =>{

    setWordEntered(filteredData[0].name)
    setCountry([])

  }
  return (
    <div className='sss'> 
    <div className="search">
      <div className="searchInputs">
        <input
          type="text"
          placeholder={placeholder}
          value={wordEntered}
          onChange={handleFilter}
        />
        <div className="searchIcon">
          {filteredData.length === 0 ? (
            <SearchIcon />
          ) : (
            <CloseIcon id="clearBtn" onClick={clearInput} />
          )}
        </div>
      </div>
      {country.length !== 0 && (
        <div className="dataResult">
          {country.slice(0, 15).map((value, key) => {
            return (
              <>
              <NavLink to={value.name} onClick={format}>  <p>{value.name} </p></NavLink>

              </>
            );
          })}
        </div>
      )}
    </div>
    <div></div>
    {filteredData[0]? <Routes>
                  <Route path={filteredData[0].name} element={

                  <TableData element={filteredData[0].name}></TableData>


                  }>
                    <Route path=':id/details' element={<Details name={filteredData[0].name}></Details>}></Route>
                  </Route>

              </Routes>:null

}



    </div>
  );
}

export default SearchBar;
